        color: #202020;
        display: block;
        min-height: 36px;
        cursor: pointer;
        position: relative;
        display: flex;
        flex-direction: row;
        justify-content: left;
        width: 100%;
      }

      .merlin-checkbox-label span.label{
        order: 2;
        padding-top: 3px;
      }

      /* // Covers cases of multiple checkboxes. */
      .merlin-checkboxes label.merlin-checkbox-label {
        min-height: 90px;
      }
      .merlin-checkboxes label.merlin-checkbox-label span.label{
        display: inline-block;
        margin: 28px 0 26px 0;
        padding-top: 0;
        padding-right: 5px;
      }

      .merlin-checkbox {
        opacity: 0;
        position: absolute;
        cursor: pointer;
      }


      .merlin-checkbox + i {
        font-style: normal;
        display: inline-block;
        width: 28px;
        height: 28px;
        border: 1px #9B9B9B solid;
        margin: 0 15px 0 0;
        background: #FFFFFF;
        color: #FFFFFF;
        position: relative;
        top: 3px;
        flex: none;
          -webkit-transition: background .5s ease;
        -moz-transition: background .5s ease;
        -o-transition: background .5s ease;
        -ms-transition: background .5s ease;
         transition: background .5s ease;}

      .merlin-checkbox-desc {
        margin-left: 50px;
        margin-bottom: 15px;
        color: #9B9B9B;
      }

      .merlin-checkboxes .merlin-checkbox + i {
        top: 30px;
        left: -5px;
        margin: 0 30px;
      }

      .merlin-checkbox:checked + i{
        background-color: #176DB6;
        border: 1px #176DB6 solid;
          -webkit-transition: background .5s ease;
        -moz-transition: background .5s ease;
        -o-transition: background .5s ease;
        -ms-transition: background .5s ease;
         transition: background .5s ease;}
      .merlin-checkbox-label i:before {
        content: "\2713";
        font-weight: 700;
        position: absolute;
        font-size: 18px;
        top: 5px;
        left: 5px;
        color: #FFFFFF;
          -webkit-transition: background .5s ease;
        -moz-transition: background .5s ease;
        -o-transition: background .5s ease;
        -ms-transition: background .5s ease;
         transition: background .5s ease;}
      .merlin-checkbox:checked + i:before {
        color: #FFFFFF;
          -webkit-transition: background .5s ease;
        -moz-transition: background .5s ease;
        -o-transition: background .5s ease;
        -ms-transition: background .5s ease;
         transition: background .5s ease;}

      .merlin-checkbox-label + i.jqError{
        display: inline-block;
          -webkit-transition: all .5s ease;
        -moz-transition: all .5s ease;
        -o-transition: all .5s ease;
        -ms-transition: all .5s ease;
         transition: all .5s ease;  color: #B1392B;
        height: 12px;
        text-indent: 33px;
        text-transform: uppercase;
        font-style: normal;
        letter-spacing: normal;
      }

