      /** Modal **/
      .ice-overlay {
        width: 100%;
        height: 100%;
        position: fixed;
        top: 0;
        left: 0;
        z-index: 9998;
        background: rgba(0, 0, 0, 0.5);
        max-width: 100%;
      }

      .ice-overlay.hidden {
        display: none;
      }

      .ice-modal {
        position: fixed;
        padding: 55px;
        background: #FFFFFF;
        left: 50%;
        top: 50%;
        -webkit-transform: translate(-50%, -50%);
        transform: translate(-50%, -50%);
        z-index: 9999;
        max-width: 900px;
      }
      .ice-modal.hidden {
        display: none;
      }

      .ice-modal-title {
        margin-top: 25px;
        margin-bottom: 60px;
      }
      body.modal-open {
        overflow-y: hidden;
      }

      i.jqError{
        margin: 2px 0 2px 12px;
        display: inline-block;
          -webkit-transition: all .5s ease;
        -moz-transition: all .5s ease;
        -o-transition: all .5s ease;
        -ms-transition: all .5s ease;
         transition: all .5s ease;  color: #B1392B;
        height: 12px;
        text-transform: uppercase;
        font-style: normal;
        letter-spacing: normal;
      }

